// Updated 07-22-21
//
// Code updated to handle country locations 
//      -- for CQ's, only Origin location is given)
//      -- for Calling Station messages, both Origin location and Target location is given
//      -- for In QSO messages, both Origin location and Target location is given
//  
//  Updated to handle 4 'word' messages, such as 'CQ DX KG4IXS FM06'
//  Updated to ignore 1 or 2 'word' messages, such as '73 RICHARD' or 'BAD COPY'
//
//  New messages added
//      + msg.has_origin_callsign
//      + msg.origin_callsign
//      + msg.has_origin_gridsquare
//      + msg.origin_gridsquare
//      + msg.has_target_callsign
//      + msg.target_callsign
//
// -----------------------------------------------------------------------------------------------------------------
//
//
// ---------------------------------------- Nothing Below Here To Modify -------------------------------------------
str = msg.qso_message;
str = str.trim();
msg.qso_message_ascii = str;
const myArr = str.split(" ");

msg.has_origin_callsign = false; msg.origin_callsign = null;
msg.has_origin_gridsquare = false; msg.origin_gridsquare = null;
msg.has_target_callsign = false; msg.target_callsign = null;
this_message_type = null;


// If nonstandard message, such as 'bad copy' or 'thanks 73' is decoded.
if (myArr.length < 3){ msg.payload = 'Nonstandard Message.'; return msg; }


// Handles a CQ calling DX/NA/OC/AS/Whatever. Last Position is Grid, Second to Last is Callsign
// Arrays start at 0, common counting starting a 1, hence the -1 offset.

last_position = (myArr.length -1);  last_pos_chars = myArr[last_position];


// Look for starting CQ in message (TX type 6) --------------------------------------

    if (myArr[0] === 'CQ') {


            calling_cq_gridsquare = last_pos_chars.match(/[A-R][A-R][0-9][0-9]/g);

                        // CQ AS VE2GHI         <- Has CQ, NO Grid, Callsign at last position 
                        // CQ AS KG4GHI FM06    <- Has CQ, Has Grid, Callsign at (last_position-1) Position

                if (calling_cq_gridsquare){
                    this_message_type = 'calling_cq';
                        msg.has_origin_callsign = true;     msg.origin_callsign = myArr[last_position-1];
                        msg.has_origin_gridsquare = true;   msg.origin_gridsquare = myArr[last_position];
                        msg.has_target_callsign = false;    msg.template_message_type = this_message_type;
                return msg;

                }   else {

                    this_message_type = 'calling_cq';
                        msg.has_origin_callsign = true;     msg.origin_callsign = myArr[last_position];
                        msg.has_origin_gridsquare = false;   msg.origin_gridsquare = null;
                        msg.has_target_callsign = false;    msg.template_message_type = this_message_type;
                return msg; 
                }
